(function () {
    tinymce.create("tinymce.plugins.ImageUploadPlugin", {
        init: function (e, t) {
            t = tinyMCE.activeEditor.getParam("imageupload_rel") || t;
            var n = tinyMCE.activeEditor.getParam("imageupload_url");
            var r = document.getElementsByTagName("body")[0];
            var i = document.createElement("link");
            i.type = "text/css";
            i.rel = "stylesheet";
            i.href = t + "/css/style.css";
            r.appendChild(i);
            e.addCommand("mceImageUpload", function () {
                $("#image_upload_type").val("tinymce");
                $("body").append('<div class="imageUploadBg"></div>');
                var t = function (e) {
                    $(".imageUploadError").html(e).show();
                    r();
                };
                var r = function () {
                    $(".imageUploadFg").remove();
                    $(".imageUploadFgLoading").remove()
                };
                var i = function () {
                    $(".imageUploadBg").remove();
                    $(".imageUploadContainer").remove()
                };
                var s = '<div class="imageUploadContainer mce-container mce-panel mce-window"><div class="imageUploadContainerInner"><div class="mce-window-head"><div class="mce-title">Загрузка изображения</div><button type="button" class="mce-close">×</button></div><form action="' + n + '/" method="POST"  enctype="multipart/form-data" id="uploadImageForm"><div class="mce-container imageUploadFormContainer" hidefocus="1" tabindex="-1"><div class="mce-container-body"><br><input type="file" name="file" accept="image/*" id="image-upload-area" class=" mce-placeholder" hidefocus="true" style="width: 258px;"></div><p class="imageUploadError"></p></div></form><div class="imageUploadConfirmCase mce-panel"><div class="mce-btn mce-primary"><button role="presentation" class="imageUploadSubmit">Загрузить</button></div><div class="mce-btn"><button role="presentation" class="imageUploadClose">Отмена</button></div></div></div></div>';
                $("body").append(s);
                $(".imageUploadBg, .imageUploadContainer .imageUploadClose, .mce-close").on("click", function () {
                    r();
                    i()
                });
                $("#uploadImageForm").iframePostForm({
                    json: true, post: function () {
                    }, complete: function (n) {
                        if (typeof n != "object" || n == null || typeof n.error == "undefined") {
                            r();
                            t("Произошла ошиюбка, проверьте формат изображения.")
                        } else {
                            if (n.error != false) {
                                switch (n.error) {
                                    case"filetype":
                                        t("Произошла ошиюбка, проверьте формат изображения.");
                                        break;
                                    default:
                                        t("Произошла ошиюбка, проверьте формат изображения.");
                                        break
                                }
                            } else {
                                if (typeof n.path != "undefined") {
                                    var s = '<img src="%s" />';
                                    e.insertContent(s.replace("%s", n.path));
                                    e.focus();
                                    r();
                                    i()
                                } else {
                                    t("Произошла ошиюбка, проверьте формат изображения.")
                                }
                            }
                        }
                    }
                });

                $(".imageUploadSubmit").on("click", function () {
                    $(".imageUploadError").html("").hide();
                    if ($("#image-upload-area").val() != "") {
                        $("body").append('<div class="imageUploadFg"></div>');
                        $("body").append('<div class="imageUploadFgLoading"></div>');
                        $("#uploadImageForm").submit()
                    } else {
                        t("Please select an image to upload.")
                    }
                })
            });
            e.addButton("imageupload", {title: "Загрузить изображения", cmd: "mceImageUpload", image: t + "/img/icon.png"})
        }, getInfo: function () {
            return {
                longname: "Загрузить изображения",
                author: "hydra",
                authorurl: "/",
                infourl: "/",
                version: "1.0.0"
            }
        }
    });
    tinymce.PluginManager.add("imageupload", tinymce.plugins.ImageUploadPlugin)
})();
